import { useEffect } from "react";
import { useGetProductListQuery } from "../toolkit/product/product.service"
import { useDispatch, useSelector } from "react-redux";
import { loadProductList } from "../toolkit/product/productSlice";
import { RootState } from "../toolkit";
import { IProduct } from "../toolkit/product/product.interface";

const Product = () => {
    const dispatch = useDispatch();
    const { isError,isLoading,data: ProductList,isSuccess : productListSuccess} = useGetProductListQuery(null);
    const  productState = useSelector((state:RootState)=>state.products.products);
    useEffect(()=>{
        if(productListSuccess) {
            dispatch(loadProductList(ProductList))
        }
    },[productListSuccess])
    if(isLoading) {
        return <p>Loading ...</p>
    }
    if(isError) {
        return <p>Error ...</p>
    }
    return (
        <div>
            Name<input type="text"/>
            Description<input type="text"/>
            Quantity<input type="text"/>
            Origin<select>
                <option>Viet nam</option>
                <option>Trung Quoc</option>
                <option>Thai lan</option>
            </select>
            <table>
                <tr>
                    <td>ID</td>
                    <td>Name</td>
                    <td>Description</td>
                    <td>Quantity</td>
                    <td>Origin</td>
                </tr>
                { (productState as IProduct[] || []).map(product=>{
                    return <tr key={product.id}>
                            <td>{product.id}</td>
                            <td>{product.name}</td>
                            <td>{product.description}</td>
                            <td>{product.quantity}</td>
                            <td>{product.origin}</td>
                        </tr>

                })

                }
            </table>
        </div>
    )
}
export default Product;
