import { createSlice,PayloadAction } from "@reduxjs/toolkit";
import { IProduct, IProductState } from "./product.interface";

const initialState : IProductState = {
    products:[]
}

const productSlice = createSlice({
    name: 'product',
    initialState : initialState,
    reducers : {
        loadProductList : (state,action: PayloadAction<IProduct[]>) => {
                state.products = action.payload
        },
        addNewStudent : (state,action: PayloadAction<IProduct>) => {
            state.products = action.payload
        }
    }
})

export const {loadProductList} = productSlice.actions;
export default productSlice.reducer;
