import { useEffect, useState } from "react";
import { useAddProductMutation, useGetProductListQuery } from "../toolkit/product/product.service"
import { useDispatch, useSelector } from "react-redux";
import { addNewProduct, loadProductList } from "../toolkit/product/productSlice";
import { RootState } from "../toolkit";
import { IProduct } from "../toolkit/product/product.interface";

const Product = () => {
    const dispatch = useDispatch();
    const { isError,isLoading,data: ProductList,isSuccess : productListSuccess} = useGetProductListQuery(null);
    const [onAddProduct] = useAddProductMutation();
    const  productState = useSelector((state:RootState)=>state.products.products);
    const [name,setName] = useState<string>('');
    const [decs,setDecs] = useState<string>('');
    const [quantity,setQuantity] = useState<number>(0);
    const [origin,setOrigin] = useState<string>('');
    useEffect(()=>{
        if(productListSuccess) {
            dispatch(loadProductList(ProductList))
        }
    },[productListSuccess])
    if(isLoading) {
        return <p>Loading ...</p>
    }
    if(isError) {
        return <p>Error ...</p>
    }
    const handleAddProduct = () => {
        const newProduct = {name:name,description: decs,quantity:quantity,origin:origin};
        onAddProduct(newProduct)
        .then(()=>{
            dispatch(addNewProduct(newProduct));
        })
    }
    return (
        <div>
            Name<input type="text" onChange={(e) => setName(e.target.value)}/>
            Description<input type="text" onChange={(e) => setDecs(e.target.value)}/>
            Quantity<input type="text" onChange={(e) => setQuantity(+e.target.value)}/>
            Origin<select onChange={(e) => setOrigin(e.target.value)}>
                <option value={'Viet nam'}>Viet nam</option>
                <option value={'Trung quoc'}>Trung Quoc</option>
                <option value={'Thai lan'}>Thai lan</option>
            </select>
            <button onClick={handleAddProduct}>Them</button>
            <table>
                <tr>
                    <td>ID</td>
                    <td>Name</td>
                    <td>Description</td>
                    <td>Quantity</td>
                    <td>Origin</td>
                </tr>
                { (productState as IProduct[] || []).map(product=>{
                    return <tr key={product.id}>
                            <td>{product.id}</td>
                            <td>{product.name}</td>
                            <td>{product.description}</td>
                            <td>{product.quantity}</td>
                            <td>{product.origin}</td>
                        </tr>

                })

                }
            </table>
        </div>
    )
}
export default Product;
